nasal.load:


var self = cmdarg();
print("Model load F-15 ", self.getPath());

var skip = 0;
var str = self.getNode("sim/multiplay/generic/string[13]");
if (str == nil) {
  skip = 1;
  #print("skip");
}

var objs = {};

var loop = func () {
  if (str.getValue() == nil) {
    settimer(loop, 0.05);
    return;
  }

  var mp_str = split(":", str.getValue());

  foreach(var mis; mp_str) {
    var mis_s = split(";", mis);
    if (size(mis_s) == 4) {
      var id = mis_s[0];
      var objModel = objs[id];
      if (objModel == nil) {
        # create model
        #print("creating "~mis);
        var n = props.globals.getNode("models", 1);
        var i = 0;
        for (i = 0; 1==1; i += 1) {
          if (n.getChild("model", i, 0) == nil) {
            break;
          }
        }
        objModel = n.getChild("model", i, 1);

        objModel.getNode("elevation",1).setDoubleValue(0);
        objModel.getNode("latitude",1).setDoubleValue(0);
        objModel.getNode("longitude",1).setDoubleValue(0);
        objModel.getNode("elevation-ft-prop",1).setValue(objModel.getPath()~"/elevation");
        objModel.getNode("latitude-deg-prop",1).setValue(objModel.getPath()~"/latitude");
        objModel.getNode("longitude-deg-prop",1).setValue(objModel.getPath()~"/longitude");
        objModel.getNode("heading-deg",1).setDoubleValue(0);
        objModel.getNode("pitch-deg",1).setDoubleValue(0);
        objModel.getNode("roll-deg",1).setDoubleValue(0);
        objModel.getNode("path",1).setValue("Aircraft/F-15/Models/Stores/MP_missile/mp_missile.xml");

        var loadNode = objModel.getNode("load", 1);
        loadNode.setBoolValue(1);

        objs[id] = objModel;
        loadNode.remove();
      }
    }            
  }

  foreach(var obj; keys(objs)) {
    var exist = 0;
    foreach(var mis; mp_str) {
      var mis_s = split(";", mis);
      if (size(mis_s) == 4) {
        var id = mis_s[0];
        var objModel = objs[id];
        if (id != obj) {
          continue;
        }
        #print("found a missile!");
        exist = 1;
        var lat = num(mis_s[1]);
        var lon = num(mis_s[2]);
        var alt = num(mis_s[3]);

        var objModel = objs[id];
        if (objModel == nil) {
          print("error: did not find mp missile.");
          return;
        }
        objModel.getNode("latitude").setDoubleValue(lat);
        objModel.getNode("longitude").setDoubleValue(lon);
        objModel.getNode("elevation").setDoubleValue(alt);
      }
    }
    if (exist == 0) {
      # remove model
      var objModel = objs[obj];
      objModel.remove();
      delete(objs, obj);
    }
  }
  if (self.getNode("valid") == 0 or self.getNode("valid") == nil) {
    return;
  }
  settimer(loop, 0.05);
}
if (skip == 0) {
    loop();
}

var livery_update = aircraft.livery_update.new("Aircraft/F-15/Models/liveries", 10 );

# Load the needed modules if it hasn't been done yet.
if (!contains(globals, "f15_net"))
{
    print("LOAD F-15 mp network", self.getPath());
    var fn_net = resolvepath("Nasal") ~ "/mp-network.nas";

    if (io.stat(fn_net) == nil)
        fn_net = getprop("/sim/fg-root") ~ "/Aircraft/F-15/Nasal/mp-network.nas";

    if (io.stat(fn_net) == nil)
        fn_net = resolvepath("Aircraft/F-15/Nasal") ~ "/mp-network.nas";

    if (io.stat(fn_net) == nil)
    fn_net = getprop("/sim/fg-root") ~ "/Aircraft/F-15/Nasal/mp-network.nas";

    print("Loading F-15 mp network from ",fn_net);
    io.load_nasal(fn_net, "f15_net");

    # Initialize network as observer.
    f15_net.mp_network_init(0);
}
