binding.script:

if (getprop("fdm/jsbsim/fcs/steer-maneuver"))
{
    gui.popupTip("Maneuver NWS disabled");
    setprop("fdm/jsbsim/fcs/steer-maneuver",0)
}
else
{
    gui.popupTip("Maneuver NWS enabled");
    setprop("fdm/jsbsim/fcs/steer-maneuver",1)
}

if (getprop("fdm/jsbsim/systems/holdback/holdback-cmd"))
{
logprint(3, "Disengage launchbar");
setprop("fdm/jsbsim/systems/catapult/cat-launch-cmd",0);
setprop("fdm/jsbsim/systems/holdback/holdback-cmd",0);
setprop("controls/gear/brake-parking",1);
}
else
{
logprint(3, "Engage launchbar");
setprop("fdm/jsbsim/systems/catapult/cat-launch-cmd",0);
setprop("fdm/jsbsim/systems/holdback/holdback-cmd",1);
setprop("controls/gear/brake-parking",0);
}


if(getprop("sim/hud/visibility[1]"))
{
    setprop("sim/hud/visibility[0]",0);
    setprop("sim/hud/visibility[1]",0);
}
else
{
    setprop("sim/hud/visibility[0]",1);
    setprop("sim/hud/visibility[1]",1);
}

var w = getprop("sim/model/f15/controls/armament/weapon-selector");
w += 1;
if (w == 3) w = 5;
if (w == 6) w = 0;
setprop("sim/model/f15/controls/armament/weapon-selector", w);

if (getprop("payload/armament/msg")==1) {
        screen.log.write("CTRL-U disabled at the moment.");
    } else {
        setprop("position/altitude-ft", getprop("position/altitude-ft")+1000);
        setprop("sim/startup/onground", 0);
    }

if (getprop("payload/armament/msg")==1) {
        screen.log.write("Speed-up disabled at the moment.");
    } else {
        controls.speedup(1);
    }

if (getprop("payload/armament/msg")) {
        screen.log.write("Speed-down disabled at the moment.");
    } else {
        controls.speedup(-1);
    }
