nasal.load:

			var self = cmdarg();
			var skip = 0;

	        var skip = 0;
	        var lat = self.getNode("rotors/main/blade[0]/flap-deg");
	        var lon = self.getNode("rotors/main/blade[1]/flap-deg");
	        var alt = self.getNode("rotors/main/blade[2]/flap-deg");
	        if (alt == nil or alt.getValue() == nil) {
	          skip = 1;
	        }

	        var objs = {};

	        var loop = func () {          

	          if(alt.getValue() != 0) {
	            var objModel = objs["first"];
	            if (objModel == nil) {
	              # create model
	              #print("creating missile");
	              var n = props.globals.getNode("models", 1);
	              var i = 0;
	              for (i = 0; 1==1; i += 1) {
	                if (n.getChild("model", i, 0) == nil) {
	                  break;
	                }
	              }
	              objModel = n.getChild("model", i, 1);

	              objModel.getNode("elevation",1).setDoubleValue(0);
	              objModel.getNode("latitude",1).setDoubleValue(0);
	              objModel.getNode("longitude",1).setDoubleValue(0);
	              objModel.getNode("elevation-ft-prop",1).setValue(objModel.getPath()~"/elevation");
	              objModel.getNode("latitude-deg-prop",1).setValue(objModel.getPath()~"/latitude");
	              objModel.getNode("longitude-deg-prop",1).setValue(objModel.getPath()~"/longitude");
	              objModel.getNode("heading-deg",1).setDoubleValue(0);
	              objModel.getNode("pitch-deg",1).setDoubleValue(0);
	              objModel.getNode("roll-deg",1).setDoubleValue(0);
	                objModel.getNode("path",1).setValue("Aircraft/f-14b/Models/Stores/MP_missile/mp_missile.xml");

	              var loadNode = objModel.getNode("load", 1);
	              loadNode.setBoolValue(1);

	              objs["first"] = objModel;
	              loadNode.remove();
	            }
	          }
	          var exist = 0;
	          if(alt.getValue() != 0) {
	            exist = 1;

	            var objModel = objs["first"];
	            if (objModel == nil) {
	              print("error: did not find mp missile.");
	              return;
	            }# else {
	            #  print("found a missile!");
	            #}
	            objModel.getNode("latitude").setDoubleValue(lat.getValue());
	            objModel.getNode("longitude").setDoubleValue(lon.getValue());
	            objModel.getNode("elevation").setDoubleValue(alt.getValue()*M2FT);

	          }
	          if (exist == 0) {
	            # remove model
	            var objModel = objs["first"];
	            if (objModel != nil) {
	              objModel.remove();
	              delete(objs, "first");
	            }
	          }

	          if (self.getNode("valid") == 0 or self.getNode("valid") == nil) {
	            return;
	          }
	          settimer(loop, 0.05);
	        }

	        if (skip == 0) {
	            loop();
	        }			

			print("LOAD f-14b ", self.getPath());

			self.getNode("sim/model/ac-type", 1).setValue("f-14b"); # Dual-control

			var livery_update = aircraft.livery_update.new(
				"Aircraft/f-14b/Models/Liveries", 10 );

			# Load the needed modules if it hasn't been done yet.
			# f14_net handles broadcast of:
			#   - external load set.
			if (!contains(globals, "f14_net")) {
            var fn_net = resolvepath("Systems") ~ "/mp-network.nas";

            if (io.stat(fn_net) == nil)
                fn_net = resolvepath("Nasal") ~ "/mp-network.nas";
            if (io.stat(fn_net) == nil)
                fn_net = resolvepath("Aircraft/f-14b/Systems") ~ "/mp-network.nas";

            if (io.stat(fn_net) == nil)
                fn_net = getprop("/sim/fg-root") ~ "/Aircraft/f-14b/Systems/mp-network.nas";

            print("Loading F-14 mp network from ",fn_net);
            io.load_nasal(fn_net, "f14_net");
				f14_net.mp_network_init(0);
			}

nasal.unload:

print("UNLOAD f-14b ", self.getPath());
cmdarg().getNode("sim/model/ac-type", 1).setValue(""); # Dual-control
livery_update.stop();
